import { useState } from 'react';

function Square() { {/* Square is a component, must start w caps, read in value prop */}
  const [value, setValue] = useState(null); {/* value stores value, setValue changes value, null og state */}

  function handleClick() {
    setValue('X');
  }
  return <button className="square" onClick={handleClick}>{value}</button>; {/*class name square is a prop */}
}

export default function Board() { //export = use outside, default = main function
  return (
    <>
      <div className="board-row"> 
        <Square /> {/* pass in value prop to each Square component */}
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
    </>
  );
}

{/* components use state to remember things */}
{/* each square has its own state */}
{/* use shared state in parent component, pass that state to child via props */}
